import React,{useState} from 'react'

function Blog(){
    const [name, setName] =useState("Mr.");
    const [i,setI]=useState(0);
    const [user,setUser]=useState({name:"Manish Yadav",class:"B.Tech",roll:"109"})
  return (
    <div>
    <div className='row'>
        <div className='col-sm-4'>
    <h1>useState example is here</h1>
  The Name={name}<br/>
  <button onClick={()=>{handlename("Manish")}}>setName</button>
  <br/>
  The Counter {i}
  <br/>
  <button onClick={()=>{handleCounter()}}>Counter</button>
  <br/>
  The user data ={JSON.stringify(user)}
  <br/>
  <br/>
  <button onClick={()=>{update({"hobbies":"Reasioning","Sports":"Cricket","City":"Gorakhpur"})}}>UpdateValue</button>
  </div>
 <div className='col-sm-8'>
 <div data-aos="zoom-out" data-aos-offset="200" data-aos-easing="ease-in-sine" data-aos-duration="600">
<img src='./images/r1.webp'/> 
</div>
 </div>
    </div>
    </div>
  );

  function handlename(x)
  {
    setName(prevName=>prevName+x);
  }
  function handleCounter()
  {
    setI(prevI=>prevI+1);
  }
  function update(data)
  {
    setUser((preUser)=>{
        return {...preUser,...data};
    });
  }
}

export default Blog;
